package testPrograms;

public class Factorial {
     static int var=5; //Factorial of A number 
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		for(i=5;i<=5;i--) {
			var=var*i;
			System.out.println("Factorial of the given Number is:" + var);
		}
 
	}

}
